import React, {Component} from 'react';
import $ from 'jquery';
import Header from '../Header/Header.js';
//import './profile.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';


class CurrentTrainings extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mentorInfo: {},
      mentorSkills:[]
    };
  }

  fetchProfileInfo() {
    const {userid} = this.oldState;
    var {mentorInfo} = this.state;
    const url = 'http://localhost:8080/mod/profile/mentor/' + userid;
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
        this.state.mentorInfo = result;
        this.setState({mentorInfo: result});
      }
    );
  }

  fetchSkillsInfo() {
    const {userid} = this.oldState;
    var {mentorSkills} = this.state;
    const url = 'http://localhost:8080/mod/mentorskills/summary/' + userid;
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
        result.map(function(item) {
          mentorSkills.push(item);
        });
        this.setState({mentorSkills: mentorSkills});
      }
    );
  }

  getSkillsTableBody() {
    var rows = [];
    const {mentorSkills} = this.state;

    for (const [index, value] of mentorSkills.entries()) {
      rows.push(
        <tr>
          <td>{value.technology}</td>
          <td>{value.experience}</td>
          <td>{value.noOfTrainings}</td>
          <td>{value.fee}</td>
        </tr>
      );
    }
    return (
      <tbody>
        {rows}
      </tbody>
    );
  }

  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    return (
      <div class="current-trainings-container">
        <h5>Current Trainings</h5>
        <div class="tabs-container">

          <div class="skill-summary">
            <table>
              <col width="200"/>
              <col width="200"/>
              <col width="200"/>
              <col width="200"/>
              <thead>
                <tr>
                  <th>Technology</th>
                  <th>Experience</th>
                  <th>No of Trainings delivered</th>
                  <th>Fee charged</th>
                </tr>
              </thead>
            </table>
          </div>
        </div>
      </div>
    );
  }
}
export default CurrentTrainings;
