import React, {Component} from 'react';
import $ from 'jquery';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import './searchtrainings.css';
import Select from 'react-select';
import DateRangePicker from 'react-daterange-picker';
import "react-daterange-picker/dist/css/react-calendar.css";
import moment from 'moment';



class SearchTrainings extends Component {
  constructor(props) {
    super(props);

    this.handleSelect = this.handleSelect.bind(this);

    this.state = {
      options: [{
        value: 'dev_v3a',
        label: '3.0 E1'  }, {
        value: 'test_v3a',
        label: '3.0 E2'
      }],
      technology: null
    };

  }

  handleSelect = name => value => {
		this.setState({[name]: value });
	}



  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    const {technology, options} = this.state;

    return (
      <div class="search-trainings-container">
        <h5>Search Trainings</h5>
        <div class="technology-container">
          <Select
    				placeholder="Select a Technology"
    				isSearchable={true}
    				isMulti={false}
    				autoFocus={false}
    				onChange={this.handleSelect('technology')}
    				options={options}
    				value={technology}
    				name="technology"
    				maxMenuHeight={200}
    			/>
        </div>
        <div class="technology-container">
          <DateRangePicker
            locale='en'
            numberOfCalendars={2}
            selectionType="range"
            minimumDate={new Date()} />
        </div>


      </div>
    );
  }
}
export default SearchTrainings;
