import React, {Component} from 'react';
import $ from 'jquery';
import Header from '../Header/Header.js';
import './profile.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';


class Profile extends Component {
  constructor(props) {
    super(props);
    this.oldState = props.location.state;
    this.state = {
      mentorInfo: null
    };
    this.fetchProfileInfo();
  }

  fetchProfileInfo() {
    const {userid} = this.oldState;
    var {mentorInfo} = this.state;
    alert(userid + mentorInfo);
    const url = 'http://localhost:8080/mod/profile/mentor/' + userid;
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
        this.state.mentorInfo = result;
        //this.setState({mentorInfo: result});
      },
    );
  }



  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    const {mentorInfo} = this.state;
    return (
      <div class="profile-container">
        <Header/>
        <div class="tabs-container">
          <Tabs>
            <TabList>
              <Tab>Profile</Tab>
            </TabList>

            <TabPanel>
              <div class="profile-details">
                <table>
                  <col width="200"/>
                  <col width="500"/>
                  <tbody>
                    <tr>
                      <td>Name</td>
                      <td>{mentorInfo.name}</td>
                    </tr>
                    <tr>
                      <td>Designation</td>
                      <td>{mentorInfo.designation}</td>
                    </tr>
                    <tr>
                      <td>Skills</td>
                      <td>{mentorInfo.skills}</td>
                    </tr>
                    <tr>
                      <td>Email</td>
                      <td>{mentorInfo.email}</td>
                    </tr>
                    <tr>
                      <td>Location</td>
                      <td>{mentorInfo.location}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </TabPanel>
          </Tabs>
        </div>
      </div>
    );
  }
}
export default Profile;
