import React, {Component} from 'react';
import $ from 'jquery';
import {connect} from 'react-redux';
import Header from '../Header/Header.js';
import './editSkills.css';
import {Constant} from '../../constants/AppConstants.js';

function mapStateToProps(state, props) {
    return {
      userId: state.userId
    };
}

class EditSkills extends Component {
  constructor(props) {
    super(props);
    this.state = {
      completedTrainings: [],
      userInfo: {},
      userId: this.props.userId
    };

    this.getUserInfo();
  }

  getUserInfo() {
    const {userId, userInfo} = this.state;
    const url = Constant.BASE_URL + '/login/userinfo/' + userId;
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
        this.state.userInfo = result;
        this.setState({userInfo: result});
      }
    );
  }

  fetchSkillsInfo() {
    const {userid} = this.oldState;
    var {mentorSkills} = this.state;
    const url = Constant.BASE_URL + '/mentorskills/summary/' + userid;
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
        result.map(function(item) {
          mentorSkills.push(item);
        });
        this.setState({mentorSkills: mentorSkills});
      }
    );
  }

  getSkillsTableBody() {
    var rows = [];
    const {mentorSkills} = this.state;

    for (const [index, value] of mentorSkills.entries()) {
      rows.push(
        <tr>
          <td>{value.technology}</td>
          <td>{value.experience} years</td>
          <td>{value.noOfTrainings}</td>
          <td>{value.fee}</td>
        </tr>
      );
    }
    return (
      <tbody>
        {rows}
      </tbody>
    );
  }

  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    return (
      <div class="edit-skills-container">
        <h5>Experience</h5>
        <table>
          <col width="200"/>
          <col width="200"/>
          <col width="200"/>
          <col width="200"/>
          <thead>
            <tr>
              <th>Technology</th>
              <th>Experience</th>
              <th>No of Trainings delivered</th>
              <th>Fee charged</th>
            </tr>
          </thead>
          {this.getSkillsTableBody()}
        </table>
      </div>
    );
  }
}
export default connect(mapStateToProps)(EditSkills);
