import React, {Component} from 'react';
import $ from 'jquery';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import Select from 'react-select'

class SearchTrainings extends Component {
  constructor(props) {
    super(props);

    this.onSelect = this.onSelect.bind(this);

    this.state = {
      options: [{
        value: 'dev_v3a',
        label: '3.0 E1'  }, {
        value: 'test_v3a',
        label: '3.0 E2'
      }],
      technology: options[0]
    };

  }

  onSelect() {

  }

  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    const {technology, options} = this.state;

    return (
      <div class="search-trainings-container">
        <h5>Search Trainings</h5>
        <Select
  				placeholder="Select a Technology"
  				isSearchable={true}
  				isMulti={false}
  				autoFocus={false}
  				onChange={this.handleSelect('technology')}
  				options={options}
  				value={technology}
  				name="technology"
  				maxMenuHeight={200}
  			/>


      </div>
    );
  }
}
export default SearchTrainings;
