import React, {Component} from 'react';
import $ from 'jquery';
import Header from '../Header/Header.js';
import './currenttrainings.css';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';


class CurrentTrainings extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inprogressTrainings: [],
      scholar: 3
    };
    this.fetchInprogressTrainings();
  }

  fetchInprogressTrainings() {
    const {scholar} = this.state;
    var {inprogressTrainings} = this.state;
    const url = 'http://localhost:8080/mod/training/scholar/' + scholar + '/status/inprogress';
    fetch(url, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }})
    .then(res => res.json())
    .then(
      (result) => {
          result.map(function(item) {
          inprogressTrainings.push(item);
        });
        this.setState({inprogressTrainings: inprogressTrainings});
      }
    );
  }

  getTable() {
    return (
      <table>
        <col width="50"/>
        <col width="200"/>
        <col width="200"/>
        <col width="50"/>
        <col width="50"/>
        <col width="50"/>
        <col width="50"/>
        <thead>
          <tr>
            <th>Training ID</th>
            <th>Technology</th>
            <th>Mentor</th>
            <th>Progress</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Status</th>
          </tr>
        </thead>
        {this.getInprogressTableBody()}
      </table>
    );
  }

  getInprogressTableBody() {
    var rows = [];
    const {inprogressTrainings} = this.state;

    for (const [index, value] of inprogressTrainings.entries()) {
      rows.push(
        <tr>
          <td>{value.id}</td>
          <td>{value.technology}</td>
          <td>{value.mentor}</td>
          <td>{value.progress}</td>
          <td>{value.startdate}</td>
          <td>{value.enddate}</td>
          <td>{value.status}</td>
        </tr>
      );
    }
    return (
      <tbody>
        {rows}
      </tbody>
    );
  }

  componentDidMount() {
  }

  componentDidUpdate() {
  }


  render() {
    return (
      <div class="current-trainings-container">
        <div class="inprogress-trainings">
          <h5>In-progress Trainings</h5>
          {this.getTable()}
        </div>
        <div class="confirmed-trainings">
          <h5>Confirmed Trainings</h5>
          {this.getTable()}
        </div>
        <div class="proposed-trainings">
          <h5>Proposed Trainings</h5>
          {this.getTable()}
        </div>
      </div>
    );
  }
}
export default CurrentTrainings;
